mapper.select:
select
  id, admin_accout, admin_name, admin_phone, admin_pwd, admin_gender, admin_address, admin_birthday
from springmanage.admin


        and id = #{id}


        and admin_accout = #{adminAccout}


        and admin_name = #{adminName}


        and admin_phone = #{adminPhone}


        and admin_pwd = #{adminPwd}


        and admin_gender = #{adminGender}


        and admin_address = #{adminAddress}


        and admin_birthday = #{adminBirthday}
